mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

mapper.select:
SELECT A.*, M.nickName AS extra_writer, B.code AS extra_boardCode FROM article A JOIN `member` M ON A.memberId = M.id JOIN board B ON A.boardId = B.id
AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{keyword}, '%')
AND A.body LIKE CONCAT('%', #{keyword}, '%')
AND M.nickName LIKE CONCAT('%', #{keyword}, '%')
ORDER BY A.id DESC LIMIT #{limitFrom}, #{itemsInAPage}

mapper.update:
UPDATE article 
SET hits = hits + 1 
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) 
FROM `like`
WHERE articleId = #{articleId} AND memberId = #{loginedMemberId}
